DROP TABLE IF EXISTS `ztv_daybugresolve`; DROP VIEW IF EXISTS `ztv_daybugresolve`;

CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`%` SQL SECURITY DEFINER VIEW `ztv_daybugresolve`
AS SELECT
   count(1) AS `bugresolve`,left(`zt_action`.`date`,10) AS `day`
FROM `zt_action` where ((`zt_action`.`objectType` = 'bug') and (`zt_action`.`action` = 'resolved')) group by left(`zt_action`.`date`,10);

# 导出视图 ztv_dayeffort
# ------------------------------------------------------------

DROP TABLE IF EXISTS `ztv_dayeffort`; DROP VIEW IF EXISTS `ztv_dayeffort`;

CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`%` SQL SECURITY DEFINER VIEW `ztv_dayeffort`
AS SELECT
   round(sum(`zt_effort`.`consumed`),1) AS `consumed`,
   `zt_effort`.`date` AS `date`
FROM `zt_effort` group by `zt_effort`.`date`;


