	(1017,'年度总结-每月用例操作情况','annualSummary_monthlyCaseAction','mysql','text',1,'bar','','0',NULL,'open',NULL,'{\"xaxis\":[{\"field\":\"actionDate\",\"name\":\"\\u65e5\\u671f\",\"group\":\"value\"}],\"yaxis\":[{\"type\":\"value\",\"field\":\"createdCases\",\"agg\":\"value\",\"name\":\"\\u521b\\u5efa\",\"valOrAgg\":\"value\"},{\"type\":\"value\",\"field\":\"toBugCases\",\"agg\":\"value\",\"name\":\"\\u8f6cBug\",\"valOrAgg\":\"value\"},{\"type\":\"value\",\"field\":\"runCases\",\"agg\":\"value\",\"name\":\"\\u6267\\u884c\",\"valOrAgg\":\"value\"}]}',NULL,0,NULL,NULL,'SELECT SUM(createdCases) AS createdCases, SUM(toBugCases) AS toBugCases, SUM(runCases) AS runCases, YEAR(CONCAT(t2.actionDate, \'-01\')) AS `year`, t1.account, realname, dept\nFROM zt_user t1\nLEFT JOIN (\n    SELECT t21.actor, LEFT(t21.`date`, 7) as actionDate,\n    SUM(IF((t22.id IS NOT NULL AND t23.id IS NULL), 1, 0)) AS createdCases,\n    SUM(IF((t22.id IS NOT NULL AND t23.id IS NOT NULL), 1, 0)) AS toBugCases,\n    SUM(IF((t24.lastRunner = t21.actor AND t21.action = \'run\' AND t21.`date` = t24.`date`), 1, 0)) AS runCases\n    FROM zt_action t21\n    LEFT JOIN zt_case t22 on t21.objectID = t22.id\n    LEFT JOIN zt_bug t23 on t22.id = t23.case\n    LEFT JOIN zt_testresult t24 on t22.id = t24.`case` AND t24.lastRunner = t21.actor AND t21.action = \'run\' AND t21.`date` = t24.`date`\n    WHERE t21.objectType = \'case\'\n    AND t21.action in (\'opened\', \'run\')\n    AND t22.deleted = \'0\'\n    AND (t23.deleted = \'0\' OR t23.id IS NULL)\n    GROUP BY t21.actor, actionDate\n) t2 on t1.account = t2.actor\nWHERE t1.deleted = \'0\'\nAND t2.actor is not null\nGROUP BY t2.actionDate, t1.account, realname, dept','published',1,NULL,'system','2024-10-26 22:22:58','',NULL,0),
