\":\"\",\"fr\":\"\"},\"deviation\":{\"zh-cn\":\"\\u504f\\u5dee\",\"zh-tw\":\"\",\"en\":\"deviation\",\"de\":\"\",\"fr\":\"\"},\"rate\":{\"zh-cn\":\"\\u504f\\u5dee\\u6bd4\\u7387\",\"zh-tw\":\"\",\"en\":\"rate\",\"de\":\"\",\"fr\":\"\"}}','select\n*,\nround(tt.deviation/tt.estimate,3) rate\nfrom(\nselect\nt1.name,\nt1.id,\nt1.closedDate,\nt2.estimate estimate,\nt2.consumed consumed,\nt2.`left`,\nt2.consumed-t2.estimate deviation\nfrom\nzt_project t1\nleft join\n(select\nproject,\nsum(estimate) estimate,\nsum(consumed) consumed,\nsum(`left`) `left`\nfrom\nzt_task\ngroup by project) t2\non t1.id=t2.project\nwhere t1.deleted=\'0\'\nand t1.status=\'closed\'\nand t1.type=\'project\') tt','published',0,NULL,'system','2024-10-26 22:22:58','',NULL,0),
