	(20010,'应用数据-项目任务概况表','appData_projectTaskOverview','mysql','text',1,'table','60','0',NULL,'open',NULL,'{\"group\":[],\"column\":[{\"field\":\"name\",\"valOrAgg\":\"value\",\"name\":\"\\u9879\\u76ee\"},{\"field\":\"createdTasks\",\"valOrAgg\":\"value\",\"name\":\"\\u65b0\\u589e\\u4efb\\u52a1\\u6570\"},{\"field\":\"contributors\",\"valOrAgg\":\"value\",\"name\":\"\\u65b0\\u589e\\u4efb\\u52a1\\u4eba\\u6570\"},{\"field\":\"finishedTasks\",\"valOrAgg\":\"value\",\"name\":\"\\u5b8c\\u6210\\u4efb\\u52a1\\u6570\"}],\"filter\":[]}',NULL,0,NULL,NULL,'SELECT\n	t1.name,\n	t4.year,\n	t4.month,\n	t1.createdTasks,\n	t2.finishedTasks,\n	t3.contributors\nFROM\n  (\nselect distinct year(date) as year, month(date) as month\nfrom zt_action\n  ) as t4\nleft join\n	(\nSELECT\n	t1.id,\n	t1.NAME,\n	YEAR ( t2.openedDate ) AS YEAR,\n	MONTH ( t2.openedDate ) AS MONTH,\n	COUNT( t2.id ) AS createdTasks\nFROM\n	zt_project AS t1\n	LEFT JOIN zt_task AS t2 ON t1.id = t2.project\nWHERE\n	t1.type = \'project\'\nGROUP BY\n	t1.id,\n	YEAR ( t2.openedDate ),\n	MONTH ( t2.openedDate )\n	) AS t1 on t4.year = t1.year and t4.month = t1.month\n	LEFT JOIN (\nSELECT\n	t1.id,\n	t1.NAME,\n	YEAR ( t2.finishedDate ) AS YEAR,\n	MONTH ( t2.finishedDate ) AS MONTH,\n	COUNT( t2.id ) AS finishedTasks\nFROM\n	zt_project AS t1\n	LEFT JOIN zt_task AS t2 ON t1.id = t2.project\nWHERE\n	t1.type = \'project\'\n	AND t2.finishedDate IS NOT NULL\nGROUP BY\n	t1.id,\n	YEAR ( t2.finishedDate ),\n	MONTH ( t2.finishedDate )\n	) AS t2 ON t1.id = t2.id\n	AND t4.YEAR = t2.YEAR\n	AND t4.MONTH = t2.\n	MONTH LEFT JOIN (\nSELECT\n	t1.id,\n	t1.NAME,\n	YEAR ( t3.date ) AS YEAR,\n	MONTH ( t3.date ) AS MONTH,\n	COUNT( DISTINCT t3.actor ) AS CONTRIBUTORS\nFROM\n	zt_project AS t1\n	LEFT JOIN zt_task AS t2 ON t1.id = t2.project\n	LEFT JOIN zt_action AS t3 ON t2.id = t3.objectID\nWHERE\n	t1.type = \'project\'\n	AND t3.objectType = \'task\'\n	AND t3.action IN ( \'opened\', \'closed\', \'finished\', \'canceled\', \'assigned\' )\nGROUP BY\n	t1.id,\n	YEAR ( t3.date ),\n	MONTH ( t3.date )\n	) AS t3 ON t1.id = t3.id\n	AND t4.YEAR = t3.YEAR\n	AND t4.MONTH = t3.MONTH\n	order by t1.id,t4.year','published',1,NULL,'system','2024-10-26 22:22:58','',NULL,0),
	(20011,'应用数据-产品测试表','appData_productTest','mysql','text',1,'table','63','0',NULL,'open',NULL,'{\"group\":[],\"column\":[{\"field\":\"name\",\"valOrAgg\":\"value\",\"name\":\"\\u4ea7\\u54c1\"},{\"field\":\"createdCases\",\"valOrAgg\":\"value\",\"name\":\"\\u65b0\\u589e\\u7528\\u4f8b\\u6570\"},{\"field\":\"avgBugsOfCase\",\"valOrAgg\":\"value\",\"name\":\"\\u7528\\u4f8b\\u5e73\\u5747Bug\\u6570\"},{\"field\":\"createdBugs\",\"valOrAgg\":\"value\",\"name\":\"\\u65b0\\u589eBug\\u6570\"},{\"field\":\"fixedBugs\",\"valOrAgg\":\"value\",\"name\":\"\\u4fee\\u590dBug\\u6570\"},{\"field\":\"avgFixedCycle\",\"valOrAgg\":\"value\",\"name\":\"Bug\\u5e73\\u5747\\u4fee\\u590d\\u5468\\u671f\"}],\"filter\":[]}',NULL,0,NULL,NULL,'SELECT * FROM\n(\nSELECT\n	t1.name,\n	t6.year,\n	t6.month,\n	IFNULL(t5.createdCases, 0) AS createdCases,\n	IFNULL(t4.relativedBugs / t5.createdCases, 0) AS avgBugsOfCase,\n	IFNULL(t1.createdBugs, 0) AS createdBugs,\n	IFNULL(t2.fixedBugs, 0) AS fixedBugs,\n	IFNULL(t3.fixedCycle / t2.fixedBugs, 0) AS avgFixedCycle\nFROM\n	(\n	select distinct year(date) as year, month(date) as month\n	from zt_action\n	) AS t6 left join\n	(\n	SELECT\n	t1.id,\n	t1.NAME,\n	YEAR ( t2.openedDate ) AS YEAR,\n	MONTH ( t2.openedDate ) AS MONTH,\n	COUNT( t2.id ) AS createdBugs\nFROM\n	zt_product AS t1\n	LEFT JOIN zt_bug AS t2 ON t1.id = t2.product\nWHERE\n	t1.deleted = \'0\'\n	AND t2.deleted = \'0\'\nGROUP BY\n	t1.id,\n	YEAR ( t2.openedDate ),\n	MONTH ( t2.openedDate )\n	) AS t1 on t1.year = t6.year AND t1.month = t6.month\n	LEFT JOIN (\nSELECT\n	t1.id,\n	t1.NAME,\n	YEAR ( t2.resolvedDate ) AS YEAR,\n	MONTH ( t2.resolvedDate ) AS MONTH,\n	COUNT( t2.id ) AS fixedBugs\nFROM\n	zt_product AS t1\n	LEFT JOIN zt_bug AS t2 ON t1.id = t2.product\nWHERE\n	t1.deleted = \'0\'\n	AND t2.deleted = \'0\'\n	AND t2.`status` = \'closed\'\n	AND t2.resolution = \'fixed\'\nGROUP BY\n	t1.id,\n	YEAR ( t2.resolvedDate ),\n	MONTH ( t2.resolvedDate )\n	) AS t2 ON t1.id = t2.id\n	AND t6.YEAR = t2.YEAR\n	AND t6.MONTH = t2.MONTH\n	LEFT JOIN (\nSELECT\n	t1.id,\n	t1.NAME,\n	YEAR ( t2.resolvedDate ) AS YEAR,\n	MONTH ( t2.resolvedDate ) AS MONTH,\n	SUM( DATEDIFF( t2.resolvedDate, t2.openedDate ) ) AS fixedCycle\nFROM\n	zt_product AS t1\n	LEFT JOIN zt_bug AS t2 ON t1.id = t2.product\nWHERE\n	t1.deleted = \'0\'\n	AND t2.deleted = \'0\'\n	AND t2.`status` = \'closed\'\n	AND t2.resolution = \'fixed\'\nGROUP BY\n	t1.id,\n	YEAR ( t2.resolvedDate ),\n	MONTH ( t2.resolvedDate )\n	) AS t3 ON t1.id = t3.id\n	AND t6.YEAR = t3.YEAR\n	AND t6.MONTH = t3.\n	MONTH LEFT JOIN (\nSELECT\n	t1.id,\n	t1.NAME,\n	YEAR ( t2.openedDate ) AS YEAR,\n	MONTH ( t2.openedDate ) AS MONTH,\n	COUNT( t3.id ) AS relativedBugs\nFROM\n	zt_product AS t1\n	LEFT JOIN zt_case AS t2 ON t1.id = t2.product\n	LEFT JOIN zt_bug AS t3 ON t2.id = t3.`case`\nWHERE\n	t2.id IS NOT NULL\n	AND t3.id IS NOT NULL\n  AND t1.deleted = \'0\'\n	AND t2.deleted = \'0\'\n	AND t3.deleted = \'0\'\nGROUP BY\n	t1.id,\n	YEAR ( t2.openedDate ),\n	MONTH ( t2.openedDate )\n	) AS t4 ON t1.id = t4.id\n	AND t6.YEAR = t4.YEAR\n	AND t6.MONTH = t4.\n	MONTH LEFT JOIN (\n SELECT\n	t1.id,\n	t1.NAME,\n	YEAR ( t2.openedDate ) AS YEAR,\n	MONTH ( t2.openedDate ) AS MONTH,\n	COUNT( t2.id ) AS createdCases\nFROM\n	zt_product AS t1\n	LEFT JOIN zt_case AS t2 ON t1.id = t2.product\nWHERE\n	t1.deleted = \'0\'\n	AND t2.deleted = \'0\'\nGROUP BY\n	t1.id,\n	YEAR ( t2.openedDate ),\n	MONTH ( t2.openedDate )\n	) AS t5 ON t1.id = t5.id\n	AND t6.YEAR = t5.YEAR\n	AND t6.MONTH = t5.MONTH\n) AS t WHERE t.name IS NOT NULL','published',1,NULL,'system','2024-10-26 22:22:58','',NULL,0),
	(20012,'应用数据-产品需求概况表','appData_productStoryOverview','mysql','text',1,'table','63','0',NULL,'open',NULL,'{\"group\":[],\"column\":[{\"field\":\"name\",\"valOrAgg\":\"value\",\"name\":\"\\u4ea7\\u54c1\"},{\"field\":\"createdStories\",\"valOrAgg\":\"value\",\"name\":\"\\u65b0\\u589e\\u7814\\u53d1\\u9700\\u6c42\\u6570\"},{\"field\":\"deliveredStories\",\"valOrAgg\":\"value\",\"name\":\"\\u4ea4\\u4ed8\\u9700\\u6c42\\u6570\"}],\"filter\":[]}',NULL,0,NULL,NULL,'SELECT * FROM\n(\nSELECT\n	t1.id,\n	t1.name,\n	t3.year,\n	t3.month,\n	IFNULL(t1.count, 0) AS createdStories,\n	IFNULL(t2.count, 0) AS deliveredStories\nFROM\n	(\n	select distinct year(date) as year, month(date) as month\n	from zt_action\n	)\n	as t3 left join\n	(\nSELECT\n	t2.id,\n	t2.NAME,\n	YEAR ( t1.openedDate ) AS YEAR,\n	MONTH ( t1.openedDate ) AS MONTH,\n	COUNT( t1.id ) AS count\nFROM\n	zt_story AS t1\n	LEFT JOIN zt_product AS t2 ON t1.product = t2.id\nWHERE\n	t2.deleted = \'0\'\n	AND t1.deleted = \'0\'\n	AND t1.type = \'story\'\nGROUP BY\n	t2.id,\n	YEAR,\nMONTH\n	) AS t1 on t3.year = t1.year and t3.month = t1.month\n	LEFT JOIN (\nSELECT\n	t1.id,\n	t1.NAME,\n	t1.YEAR,\n	t1.MONTH,\n	COUNT( distinct t1.story ) AS count\nFROM\n	(\nSELECT\n	t2.id,\n	t2.NAME,\n	YEAR ( t3.date ) AS YEAR,\n	MONTH ( t3.date ) AS MONTH,\n	t1.id AS story\nFROM\n	zt_story AS t1\n	LEFT JOIN zt_product AS t2 ON t1.product = t2.id\n	LEFT JOIN ( SELECT objectID, MAX( date ) AS date FROM zt_action WHERE objectType = \'story\' AND action = \'linked2release\' GROUP BY objectID ) AS t3 ON t1.id = t3.objectID\nWHERE\n	t1.deleted = \'0\'\n	AND t2.deleted = \'0\'\n	AND EXISTS ( SELECT 1 FROM zt_action WHERE objectID = t1.id AND objectType = \'story\' AND action = \'linked2release\' )\nUNION\nSELECT\n	t2.id,\n	t2.NAME,\n	YEAR ( t1.closedDate ) AS YEAR,\n	MONTH ( t1.closedDate ) AS MONTH,\n	t1.id AS story\nFROM\n	zt_story AS t1\n	LEFT JOIN zt_product AS t2 ON t1.product = t2.id\nWHERE\n	t1.deleted = \'0\'\n	AND t2.deleted = \'0\'\n	AND t1.status = \'closed\'\n	AND t1.closedReason = \'done\'\n	) AS t1\nGROUP BY\n	t1.id,\n	t1.name,\n	t1.YEAR,\n	t1.MONTH order by id asc\n	) AS t2 ON t1.id = t2.id\n	AND t3.YEAR = t2.YEAR\n	AND t3.MONTH = t2.MONTH\n) AS t\nWHERE t.name IS NOT NULL','published',1,NULL,'system','2024-10-26 22:22:58','',NULL,0),
