	(1000,2,'86','finishedProjectDuration','mysql','text','{\"zh-cn\":\"\\u5b8c\\u6210\\u9879\\u76ee\\u5de5\\u671f\\u900f\\u89c6\\u8868\",\"zh-tw\":\"\",\"en\":\"\",\"de\":\"\",\"fr\":\"\"}',NULL,'open',NULL,'select\n    t1.name,\n    t2.program1,\n    t1.begin,\n    t1.end,\n    t1.realBegan,\n    t1.realEnd,\n    t1.closedDate,\n    t1.realduration,\n    t1.realduration - t1.planduration as duration_deviation,\n    round((t1.realduration - t1.planduration) / t1.planduration, 3) as rate\nfrom\n    (select\n        name,\n        substr(path,2,4) as program1,\n        begin,\n        end,\n        realBegan,\n        realEnd,\n        left(closedDate, 10) as closedDate,\n        datediff(`end`, `begin`) as planduration,\n        ifnull(if(left(`realEnd`,4) != \'0000\',datediff(`realEnd`,`realBegan`),datediff(`closedDate`,`realBegan`)),0) realduration\n    from zt_project\n    where type=\'project\' and status=\'closed\' and deleted=\'0\') t1\nleft join\n    (select\n        id as programid,\n        name as program1\n    from zt_project\n    where type=\'program\'\n    and grade=1) t2\non t1.program1=t2.programid;','{\"name\":{\"object\":\"project\",\"field\":\"name\",\"type\":\"string\"},\"program1\":{\"object\":\"project\",\"field\":\"program1\",\"type\":\"string\"},\"begin\":{\"object\":\"project\",\"field\":\"begin\",\"type\":\"date\"},\"end\":{\"object\":\"project\",\"field\":\"end\",\"type\":\"date\"},\"realBegan\":{\"object\":\"project\",\"field\":\"realBegan\",\"type\":\"date\"},\"realEnd\":{\"object\":\"project\",\"field\":\"realEnd\",\"type\":\"date\"},\"closedDate\":{\"object\":\"project\",\"field\":\"closedDate\",\"type\":\"date\"},\"realduration\":{\"object\":\"project\",\"field\":\"realduration\",\"type\":\"number\"},\"duration_deviation\":{\"object\":\"project\",\"field\":\"duration_deviation\",\"type\":\"number\"},\"rate\":{\"object\":\"project\",\"field\":\"rate\",\"type\":\"number\"}}','{\"name\":{\"zh-cn\":\"\\u9879\\u76ee\\u540d\\u79f0\",\"zh-tw\":\